import React, { useState, useEffect, useCallback } from 'react';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import './PartnershipSlider.css';

const PartnershipSlider = ({ slides = [] }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = useCallback(() => {
    setCurrentSlide(current => (current === slides.length - 1 ? 0 : current + 1));
  }, [slides.length]);

  const prevSlide = () => {
    setCurrentSlide(current => (current === 0 ? slides.length - 1 : current - 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  // Auto-slide every 6 seconds (longer for reading)
  useEffect(() => {
    const timer = setInterval(() => {
      nextSlide();
    }, 6000);

    return () => clearInterval(timer);
  }, [nextSlide]);

  if (slides.length === 0) {
    return null;
  }

  return (
    <div className="partnership-slider">
      <div className="partnership-slider-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
        {slides.map((slide, index) => (
          <div key={index} className={`partnership-slide ${!slide.image ? 'text-only' : ''}`}>
            <div className="partnership-content">
              <div className="partnership-logos">
                <img
                  src="/logo.png"
                  alt="PMI IT Logo"
                  className="partnership-logo"
                />
                <img
                  src="/futurecities-logo.png"
                  alt="FutureCitiesCouncil Inc. Logo"
                  className="partnership-logo"
                />
              </div>
              <h2 className="partnership-title">{slide.title}</h2>
              <h3 className="partnership-subtitle">{slide.subtitle}</h3>
              <div className="partnership-description">
                {slide.description.split('\n\n').map((paragraph, idx) => (
                  <p key={idx}>{paragraph}</p>
                ))}
              </div>
            </div>
            {slide.image && (
              <div className="partnership-image">
                <img 
                  src={slide.image} 
                  alt={slide.title}
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Navigation Arrows */}
      <button className="partnership-nav prev" onClick={prevSlide}>
        <FaChevronLeft />
      </button>
      <button className="partnership-nav next" onClick={nextSlide}>
        <FaChevronRight />
      </button>

      {/* Slide Counter */}
      <div className="partnership-counter">
        {currentSlide + 1} / {slides.length}
      </div>

      {/* Dots Indicator */}
      <div className="partnership-dots">
        {slides.map((_, index) => (
          <button
            key={index}
            className={`partnership-dot ${index === currentSlide ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default PartnershipSlider;
