import React, { useState, useEffect } from 'react';
import './Countries.css';

const Countries = () => {
  const countries = [
    {
      name: 'Bahrain',
      flag: 'ðŸ‡§ðŸ‡­',
      capital: 'Manama',
      color: '#CE1126'
    },
    {
      name: 'United Arab Emirates',
      flag: 'ðŸ‡¦ðŸ‡ª',
      capital: 'Abu Dhabi',
      color: '#00732F'
    },
    {
      name: 'Kuwait',
      flag: 'ðŸ‡°ðŸ‡¼',
      capital: 'Kuwait City',
      color: '#009639'
    },
    {
      name: 'Qatar',
      flag: 'ðŸ‡¶ðŸ‡¦',
      capital: 'Doha',
      color: '#8D1B3D'
    }
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === countries.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? countries.length - 1 : prev - 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  // Auto-slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  return (
    <div className="countries">
      <div className="countries-content">
        <div className="container">
          <div className="countries-slider">
            <button className="slider-nav prev" onClick={prevSlide} aria-label="Previous Country">&#8592;</button>
            <div className="countries-slider-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
              {countries.map((country, index) => (
                <div 
                  key={index} 
                  className="country-card slider-card"
                  style={{ '--country-color': country.color }}
                >
                  <div className="country-flag">
                    <span className="flag-emoji">{country.flag}</span>
                  </div>
                  <div className="country-info">
                    <h3>{country.name}</h3>
                    <p className="country-capital">Capital: {country.capital}</p>
                  </div>
                  <div className="country-overlay">
                    <div className="overlay-content">
                      <h3>Ready to work with {country.name}?</h3>
                      <p>Let's discuss your technology needs</p>
                      <button 
                        className="contact-btn"
                        onClick={() => {
                          const phoneNumber = '+1234567890';
                          const message = `Hi! I'm interested in your services in ${country.name}. Can you provide more information?`;
                          const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
                          window.open(whatsappUrl, '_blank');
                        }}
                      >
                        Contact Us
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <button className="slider-nav next" onClick={nextSlide} aria-label="Next Country">&#8594;</button>
          </div>
          <div className="slider-dots">
            {countries.map((_, idx) => (
              <button
                key={idx}
                className={`slider-dot ${currentSlide === idx ? 'active' : ''}`}
                onClick={() => goToSlide(idx)}
                aria-label={`Go to country ${idx + 1}`}
              />
            ))}
          </div>
          <div className="regional-stats">
            <h2>Regional Impact</h2>
            <div className="stats-grid">
              <div className="stat-item">
                <div className="stat-number">4</div>
                <div className="stat-label">Countries Served</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">50+</div>
                <div className="stat-label">Projects Completed</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">100%</div>
                <div className="stat-label">Client Satisfaction</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">24/7</div>
                <div className="stat-label">Support Available</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Countries;
