import React, { useState, useEffect } from 'react';
import { FaRegWindowMaximize, FaMobileAlt, FaCloud, FaCogs, FaDatabase, FaRobot, FaLaptopCode, FaNetworkWired, FaServer, FaChartBar, FaLock, FaGlobe, FaProjectDiagram, FaUserCircle } from 'react-icons/fa';
import PageLoader from '../components/PageLoader';
import { useImageLoading } from '../hooks/useLoadingState';
import './Countries.css';

const ICON_POOL = [
  FaRegWindowMaximize, FaMobileAlt, FaCloud, FaCogs, FaDatabase, FaRobot, FaLaptopCode, FaNetworkWired, FaServer, FaChartBar, FaLock, FaGlobe, FaProjectDiagram, FaUserCircle
];
const ICON_COLORS = [
  '#667eea', '#764ba2', '#6c63ff', '#00bcd4', '#ff9800', '#4caf50', '#e91e63', '#2196f3', '#00bfae', '#ff4081', '#b388ff', '#00e676', '#ff1744', '#ffd600'
];

function getRandom(min, max) {
  return Math.random() * (max - min) + min;
}

const Countries = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [animatedNumbers, setAnimatedNumbers] = useState({
    projects: 0,
    countries: 0,
    clients: 0
  });

  const countries = [
    {
      name: 'Bahrain',
      flag: 'https://flagcdn.com/w320/bh.png',
      capital: 'Manama',
      color: '#CE1126'
    },
    {
      name: 'United Arab Emirates',
      flag: 'https://flagcdn.com/w320/ae.png',
      capital: 'Abu Dhabi',
      color: '#00732F'
    },
    {
      name: 'Kuwait',
      flag: 'https://flagcdn.com/w320/kw.png',
      capital: 'Kuwait City',
      color: '#009639'
    },
    {
      name: 'Qatar',
      flag: 'https://flagcdn.com/w320/qa.png',
      capital: 'Doha',
      color: '#8D1B3D'
    },
    {
      name: 'Saudi Arabia',
      flag: 'https://flagcdn.com/w320/sa.png',
      capital: 'Riyadh',
      color: '#006C35'
    }
  ];

  // Use the custom hook for image loading
  const { isLoading, loadedCount, totalImages } = useImageLoading(
    countries.map(country => country.flag)
  );

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === countries.length - 1 ? 0 : prev + 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  // Auto-slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  // Animate numbers
  useEffect(() => {
    if (!isLoading) {
      const animateNumbers = () => {
        const targets = { projects: 100, countries: 7, clients: 500 };
        const duration = 2000;
        const steps = 60;
        const stepDuration = duration / steps;

        let currentStep = 0;
        const timer = setInterval(() => {
          currentStep++;
          const progress = currentStep / steps;

          setAnimatedNumbers({
            projects: Math.floor(targets.projects * progress),
            countries: Math.floor(targets.countries * progress),
            clients: Math.floor(targets.clients * progress)
          });

          if (currentStep >= steps) {
            clearInterval(timer);
            setAnimatedNumbers(targets);
          }
        }, stepDuration);

        return () => clearInterval(timer);
      };

      // Start animation after a delay
      const timeout = setTimeout(animateNumbers, 500);
      return () => clearTimeout(timeout);
    }
  }, [isLoading]);

  if (isLoading) {
    return (
      <div className="countries">
        <PageLoader 
          text={`Loading flags... (${loadedCount}/${totalImages})`}
          size="large" 
          variant="centered"
        />
      </div>
    );
  }

  return (
    <div className="countries">
      {/* Floating Icons */}
      <div className="floating-icons">
        {ICON_POOL.map((Icon, index) => (
          <div key={index} className="icon">
            <Icon size={24} color={ICON_COLORS[index % ICON_COLORS.length]} />
          </div>
        ))}
      </div>

      <div className="countries-content">
        <div className="container">
          <div className="countries-title">
            <h1>Our Regional Presence</h1>
          </div>
          <div className="countries-slider">
            <div className="countries-slider-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
              {countries.map((country, index) => (
                <div 
                  key={index} 
                  className="country-card slider-card"
                  style={{ '--country-color': country.color }}
                >
                  <div className="country-flag">
                    <img 
                      src={country.flag} 
                      alt={`${country.name} flag`} 
                      className="flag-image"
                    />
                  </div>
                  <div className="country-info">
                    <h3>{country.name}</h3>
                    <p className="country-capital">Capital: {country.capital}</p>
                  </div>
                  <div className="country-overlay">
                    <div className="overlay-content">
                      <h3>Ready to work with {country.name}?</h3>
                      <p>Let's discuss your technology needs</p>
                      <button 
                        className="contact-btn"
                        onClick={() => {
                          const phoneNumber = '+1234567890';
                          const message = `Hi! I'm interested in your services in ${country.name}. Can you provide more information?`;
                          const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
                          window.open(whatsappUrl, '_blank');
                        }}
                      >
                        Contact Us
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className="slider-dots">
            {countries.map((_, idx) => (
              <button
                key={idx}
                className={`slider-dot ${currentSlide === idx ? 'active' : ''}`}
                onClick={() => goToSlide(idx)}
                aria-label={`Go to country ${idx + 1}`}
              />
            ))}
          </div>
          <div className="goals-dashboard">
            <h2>Our 2025 Goals</h2>
            <div className="timeline-container">
              <div className="timeline-line"></div>
              <div className="goals-timeline">
                <div className="goal-column">
                  <div className="goal-item">
                    <div className="goal-icon">üöÄ</div>
                    <div className="goal-number">{animatedNumbers.projects}+</div>
                    <div className="goal-label">Projects Completed</div>
                    <div className="goal-description">Expanding our portfolio across the Gulf region</div>
                    <div className="timeline-dot"></div>
                  </div>
                </div>
                <div className="goal-column">
                  <div className="goal-item">
                    <div className="goal-icon">üåç</div>
                    <div className="goal-number">{animatedNumbers.countries}</div>
                    <div className="goal-label">Countries Served</div>
                    <div className="goal-description">Establishing presence in all Gulf countries including Tunisia</div>
